targets.expr:
max by (node_name) (
  node_memory_MemTotal_bytes{job="node-exporter", node_name=~"$nodes"}
  - node_memory_MemFree_bytes{job="node-exporter", node_name=~"$nodes"}
  - node_memory_Buffers_bytes{job="node-exporter", node_name=~"$nodes"}
  - node_memory_Cached_bytes{job="node-exporter", node_name=~"$nodes"}
)

max(
  (
    sum(
      node_memory_MemTotal_bytes{job="node-exporter", node_name=~"$nodes"}
    - node_memory_MemFree_bytes{job="node-exporter", node_name=~"$nodes"}
    - node_memory_Buffers_bytes{job="node-exporter", node_name=~"$nodes"}
    - node_memory_Cached_bytes{job="node-exporter", node_name=~"$nodes"}
    )
    /
    sum(
      node_memory_MemTotal_bytes{job="node-exporter", node_name=~"$nodes"}
    )
  ) * 100)

max(
  (
    (
      node_memory_MemTotal_bytes{job="node-exporter", node_name="$node"}
    - node_memory_MemFree_bytes{job="node-exporter", node_name="$node"}
    - node_memory_Buffers_bytes{job="node-exporter", node_name="$node"}
    - node_memory_Cached_bytes{job="node-exporter", node_name="$node"}
    )
    / node_memory_MemTotal_bytes{job="node-exporter", node_name="$node"}
  ) * 100)
